  const drag = (e) => {
    this.myRef = React.createRef();
    const node = this.myRef.current;
    let pos1 = 0,
    pos2 = 0,
    pos3 = 0,
    pos4 = 0;

    pos1 = pos3 - e.clientX;
    pos2 = pos4 - e.clientY;
