import React from "react";
import ReactDOM from "react-dom";

const SuspendMenu = props => {

  // dragElement(document.getElementById("mydiv"));
  let menu = document.querySelector(".suspended-menu");

  const FancyButton = React.forwardRef((props, ref) => (
    <button ref={ref} className="FancyButton">
      {props.children}
    </button>
  ));

  // Você agora pode obter a ref diretamente para o button do DOM:
  const ref = React.createRef();
  ;

  const drag = (e) => {
    this.myRef = React.createRef();
    const node = this.myRef.current;
    let pos1 = 0,
    pos2 = 0,
    pos3 = 0,
    pos4 = 0;

    pos1 = pos3 - e.clientX;
    pos2 = pos4 - e.clientY;
    pos3 = e.clientX;
    pos4 = e.clientY;
    console.log('======>>> e: ', e)
    console.log('======>>> clientX: ', e.clientX)
    console.log('======>>> clientY: ', e.clientY)
    // e.style.top = e.offsetTop - pos2 + "px";
    // e.style.left = e.offsetLeft - pos1 + "px";

  }
  return (
    <div>
      {<FancyButton ref={ref}>Click me!</FancyButton>}
      <div ref={this.myRef} className="suspended-menu" onDrag={e => drag(e)} >
        <a href="/main" >Main</a>
      </div>
      <div className="suspended-menu">
        <a href="/perfil">Perfil</a>
      </div>
      <div className="suspended-menu">
        <a href="/perfil">Logoff</a>
      </div>
    </div>
  )
};

export default SuspendMenu;
