import React from "react";
import ReactDOM from "react-dom";

const SuspendMenu = props => {
  assignRef = element => {
    this.container = element;
  }
  getStyle = () => {

    const styles = this.container.style;
    console.log(styles);
    // for getting computed styles
    const computed = window.getComputedStyle(this.container) // border-radius can be replaced with any other style attributes;
    console.log(computed);
  }

  // dragElement(document.getElementById("mydiv"));
  let menu = document.querySelector(".suspended-menu");

  const drag = (e) => {
    let pos1 = 0,
    pos2 = 0,
    pos3 = 0,
    pos4 = 0;

    pos1 = pos3 - e.clientX;
    pos2 = pos4 - e.clientY;
    pos3 = e.clientX;
    pos4 = e.clientY;
    console.log('======>>> e: ', e)
    console.log('======>>> clientX: ', e.clientX)
    console.log('======>>> clientY: ', e.clientY)
    // e.style.top = e.offsetTop - pos2 + "px";
    // e.style.left = e.offsetLeft - pos1 + "px";

  }
  return (
    <div>
      <div className="suspended-menu" onDrag={e => drag(e)} >
        <a href="/main" >Main</a>
      </div>
      <div className="suspended-menu">
        <a href="/perfil">Perfil</a>
      </div>
      <div className="suspended-menu">
        <a href="/perfil">Logoff</a>
      </div>
    </div>
  )
};

export default SuspendMenu;
